import React, { useState } from 'react';
import MediaIcon from '../media-icon/media-icon';
import { ClosePopUp, PopUpContainer, PopUpContent } from './media-switch.styles'

export const SwitchPopUp = ({ toggle }) => {
    const toggleSwitch = () => {
        toggle();
    }
    return (
        <PopUpContainer>
            <PopUpContent>
                <ClosePopUp onClick={toggleSwitch}>
                    &times;
                </ClosePopUp>
            </PopUpContent>
        </PopUpContainer>
    )
}

const MediaSwitch = () => {
    const [switcher, setSwitcher] = useState(false);
    const toggleSwitch = () => {
        setSwitcher(!switcher);
        console.log("STATE");
    }
    return (
        <div>
            <MediaIcon onClick={toggleSwitch} >
                <button></button>
            </MediaIcon>
            {
                switcher ?
                    <SwitchPopUp toggle={toggleSwitch} />
                    :
                    null
            }
        </div>
    )
}

export default MediaSwitch;
