import styled, { css } from 'styled-components';

const positive = css`
    color: green;
`;

const negative = css`
    color: red;
`;

const neutral = css`
    color: black;
`;
export const CommentContainer = styled.div`
    font-size: 15px;
    color: black;
    text-align: left;
    padding: 15px;
`;
