import React, { useState } from "react";
import axios from "axios";
import { websitePrefix } from "../../api-info";
import Input from "../../../components/input/input";
import Button from "../../../components/button/button";
import { useDispatch } from "react-redux";
import {
  startLoading,
  cancelLoading,
} from "../../../redux/loading/loading.actions";
import { SignInContainer, ButtonContainer, BoldSpan } from "./sign-in.styles";

export const SignInAPI = (username, password) => {
  return axios({
    url: websitePrefix + "api/log-login-attempt/",
    method: "POST",
    data: {
      "user-id": username,
      "user-password": password,
    },
    headers: { "Content-Type": "application/json" },
    mode: "cors",
  });
};

const SignIn = () => {
  const [username, setUsername] = useState("");
  const [pass, setPass] = useState("");
  const [invalid, setInvalid] = useState("");
  const dispatch = useDispatch();

  const onUserChange = (event) => {
    setUsername(event.target.value);
  };

  const onPassChange = (event) => {
    setPass(event.target.value);
  };

  const onButtonRequest = () => {
    dispatch(startLoading());
    SignInAPI(username, pass)
      .then((response) => {
        if (response.config.data === false) {
          setInvalid("Wrong Credentials, try again");
        } else {
          setInvalid("");
          console.log(JSON.parse(response.config.data));
        }
        dispatch(cancelLoading());
      })
      .catch((error) => {
        dispatch(cancelLoading());
        alert("Something went wrong, Recorded " + error);
      });
  };

  return (
    <SignInContainer>
      <Input
        name="username"
        type="text"
        label="Username"
        value={username}
        inputChange={onUserChange}
      />
      <Input
        name="password"
        type="password"
        label="Password"
        value={pass}
        inputChange={onPassChange}
      />
      <BoldSpan>{invalid}</BoldSpan>
      <ButtonContainer>
        <Button onClick={onButtonRequest} label="Sign In" />
      </ButtonContainer>
    </SignInContainer>
  );
};

export default SignIn;
