import styled, { css } from "styled-components";

const positive = css`
  color: green;
`;

const negative = css`
  color: red;
`;

const neutral = css`
  color: blue;
`;

const sentiment = (props) => {
  if (props.commentSentiment.NEU < props.commentSentiment.POS) {
    if (props.commentSentiment.POS < props.commentSentiment.NEG) {
      return negative;
    } else {
      return positive;
    }
  } else {
    return neutral;
  }
};

export const CommentContainer = styled.div`
  font-size: 15px;
  text-align: left;
  padding: 15px;
  ${sentiment}
`;
