import styled from "styled-components";
import Modal from "react-modal";

export const PopUpContent = styled(Modal)`
  background-color: black;
  color: white;
  position: absolute;
  text-align: center;
  top: 20%;
  left: 29%;
  width: 40%;
  padding: 20px;
  border-radius: 5px;
  border: 2px solid black;
  :focus {
    outline: none;
  }
`;

export const PreferanceContainer = styled.div`
  text-align: center;
`;
