import styled from 'styled-components';

export const PopUpContainer = styled.div`
    position: fixed;
    background: #00000050;
    width: 100%;
    height: 100vh;
    top: 0;
    left: 0;
`;

export const PopUpContent = styled.div`
    position: relative;
    width: 70%;
    margin: 0 auto;
    height: auto;
    max-height: 70vh;
    margin-top: calc(100vh - 85vh - 20px);
    background: #fff;
    border-radius: 4px;
    padding: 20px;
    border: 1px solid #999;
    overflow: auto;
`;

export const ClosePopUp = styled.span`
    float: right;
    :hover{
        color: red;
        cursor: pointer;
    }
`;
