import React, { useState } from 'react';
import MediaIcon from '../media-icon/media-icon';
import { IconButton } from '@material-ui/core';
import { useDispatch } from 'react-redux';
import {
    ClosePopUp,
    PopUpContainer,
    PopUpContent,
    YoutubeIconStyled,
    TwitterIconStyled,
    RedditIconStyled,
    AmazonIconStyled
} from './media-switch.styles'

export const SwitchPopUp = ({ toggle }) => {
    const dispatch = useDispatch();
    const toggleSwitch = () => {
        toggle();
    }
    // const ChangePref = (media) => {
    //     toggle();
    //     switch (media) {
    //         case value:

    //             break;
    //         default:
    //             break;
    //     }
    // }
    return (
        <PopUpContainer>
            <PopUpContent>
                <ClosePopUp onClick={toggleSwitch}>
                    &times;
                </ClosePopUp>
                <h2>
                    Choose Prefered Media
                </h2>
                <IconButton onClick={toggleSwitch}>
                    <YoutubeIconStyled />
                </IconButton>
                <IconButton onClick={toggleSwitch}>
                    <TwitterIconStyled />
                </IconButton>
                <IconButton onClick={toggleSwitch}>
                    <RedditIconStyled />
                </IconButton>
                <IconButton onClick={toggleSwitch}>
                    <AmazonIconStyled />
                </IconButton>
            </PopUpContent>
        </PopUpContainer>
    )
}

const MediaSwitch = () => {
    const [switcher, setSwitcher] = useState(false);
    const toggleSwitch = () => {
        setSwitcher(!switcher);
        console.log("STATE");
    }
    return (
        <div>
            <IconButton onClick={toggleSwitch}>
                <MediaIcon />
            </IconButton>
            {
                switcher ?
                    <SwitchPopUp toggle={toggleSwitch} />
                    :
                    null
            }
        </div>
    )
}

export default MediaSwitch;
