import styled from 'styled-components';

export const PopUpContainer = styled.div`
    position: fixed;
    background: #00000050;
    width: 100%;
    height: 100vh;
    top: 0;
    left: 0;
`;

export const PopUpContent = styled.div`
    background-color: black;
    position: absolute;
    top: 20%;
    left: 30%;
    width: 40%;
    padding: 20px;
    border-radius: 5px;
    border: 2px solid black;
`;

export const ClosePopUp = styled.span`
    float: right;
    :hover{
        color: red;
        cursor: pointer;
    }
`;
