import styled, { css } from 'styled-components';

const positive = css`
    color: green;
`;

const negative = css`
    color: red;
`;

const neutral = css`
    color: black;
`;

const stats = css`
    visibility: hidden;
    width: 120px;
    background-color: black;
    color: #fff;
    text-align: center;
    border-radius: 6px;
    padding: 5px 0;
    position: absolute;
    z-index: 1;
`;

const sentiment = (props) => {
    if (props.sentiment > 0.1) {
        return positive;
    } else if (props.sentiment < -0.1) {
        return negative;
    } else {
        return neutral;
    }
}

export const CommentContainer = styled.div`
    font-size: 15px;
    text-align: left;
    padding: 15px;
    cursor: pointer;
    ${sentiment}

    :hover{
        ${stats}
        visibility: visible;
    }
`;
