import React, { useState } from 'react';
import MediaIcon from '../media-icon/media-icon';
import { IconButton } from '@material-ui/core';
import {
    ClosePopUp,
    PopUpContainer,
    PopUpContent,
    YoutubeIconStyled,
    TwitterIconStyled,
    RedditIconStyled,
    AmazonIconStyled
} from './media-switch.styles'

export const SwitchPopUp = ({ toggle }) => {
    const toggleSwitch = () => {
        toggle();
    }
    return (
        <PopUpContainer>
            <PopUpContent>
                <ClosePopUp onClick={toggleSwitch}>
                    &times;
                </ClosePopUp>
                <h1>
                    Choose Prefered Media
                </h1>
                <YoutubeIconStyled />
                <TwitterIconStyled />
                <RedditIconStyled />
                <AmazonIconStyled />
            </PopUpContent>
        </PopUpContainer>
    )
}

const MediaSwitch = () => {
    const [switcher, setSwitcher] = useState(false);
    const toggleSwitch = () => {
        setSwitcher(!switcher);
        console.log("STATE");
    }
    return (
        <div>
            <IconButton onClick={toggleSwitch}>
                <MediaIcon />
            </IconButton>
            {
                switcher ?
                    <SwitchPopUp toggle={toggleSwitch} />
                    :
                    null
            }
        </div>
    )
}

export default MediaSwitch;
