import React, { useEffect, useState } from 'react'
import { Route } from 'react-router-dom'
import Navigation from './views/navigation/navigation'
import HomePage from './views/homepage/homepage';
import CommentExtractor from './views/comment-extractor/comment-extractor'
import LoadScreen from './views/load-screen/load-screen'
import { useSelector, useDispatch } from 'react-redux';
import './App.css';

export const App = () => {

  // const [loading, setLoading] = useState(useSelector(state => state.loading));
  // const dispatch = useDispatch();
  const loading = useSelector(state => state.loading);
  console.log(loading);
  return (
    <div className="App">
      {
        loading
          ?
          <LoadScreen />
          :
          <div>
            <Route exact={false} path='/' component={Navigation} />
            <Route exact path='/' component={HomePage} />
            <Route exact path='/search' component={CommentExtractor} />
          </div>
      }
      <button onClick={() => console.log(loading)}>CLICK</button>
    </div>
  );
}

export default App;
