import styled, { css } from "styled-components";

const positive = css`
  color: green;
`;

const negative = css`
  color: red;
`;

const neutral = css`
  color: blue;
`;

const sentiment = (props) => {
  const neu = props.sentiment.NEU;
  const pos = props.sentiment.POS;
  const neg = props.sentiment.NEG;
  const sent = {
    neu: negative,
    pos: positive,
    neg: negative,
  };
  return sent.Math.max(neu, Math.max(pos, neg))
};

export const CommentContainer = styled.div`
  font-size: 15px;
  text-align: left;
  padding: 15px;
  ${sentiment}
`;
